package ex;

public class Ex2_6 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int x = 100; // 정수형 int or long
		double pi = 3.14; // 실수형 double or float
		char ch = 'a'; // 문자 쌍따옴표 쓰면 안됨. 쌍따옴표는 string에만. 홑따옴표 ' ' 사
		String str = "abc"; // 문자열 - 쌍따옴표 " "
		
		System.out.println(x);
		System.out.println(pi);
		System.out.println(ch);
		System.out.println(str);
	}

}
